import React, { Component } from "react";
import "./SubTaskTable.css";

class SubTaskTable extends Component {
  constructor(props){
    super(props);
    this.state = {
        value:""
    };
    this.style = {
        padding: "10px",
        width: "310px",
        border: "2px solid #eff0f2",
        margin: "10px"
      }
  }

  addSubTask = (value) => {
    this.props.addSubTask(value);
 }

 onKeyPress = event => {
    if (event.key === 'Enter') {
        this.setState({ value: event.target.value })
       let value = this.state.value;
       this.addSubTask(value);
       event.target.value="";
 }
}

  render() {

    return (
      <div className="SubTaskTable">
          <div className="SubTaskTableCard">
          {/* <form onSubmit={() => this.addSubTask(this.inputElement)}>
                  <input style={this.style} ref={(a) => this.inputElement =a}
                  placeholder="Add a new sub task">
                  </input>
                </form> */}
                <input type="text" style={this.style} onKeyPress={this.onKeyPress} placeholder='Add a new sub task'/> 
          </div>       
      </div>
    );
  }
}

export default SubTaskTable;
