import React, { Component } from "react";
import TodayTasks from './TodayTasks';
import TomorrowTasks from './TomorrowTasks';
import SomedayTasks from './SomedayTasks';
import "./Tasks.css";

class Tasks extends Component {
  constructor(props){
    super(props);
    this.state = {
     Todaytasks:[],
     showTodayTasks:false,
     showTomorrowTasks:false,
     showSomedayTasks:false,
    };
  }

  // display(name){
  //  var match = "false";
  //  let items=this.props.items;
  //  if(name === "Today"){
  //    this.setState({showTodayTasks:!this.state.showTodayTasks});
  //  }
  //  else if(name === "Tomorrow"){
  //   this.setState({showTomorrowTasks:!this.state.showTomorrowTasks});
  //  }
  //  else if(name === "Someday"){
  //   this.setState({showSomedayTasks:!this.state.showSomedayTasks});
  //  }
  // this.setState({show:name});
  //  for(let i=0;i<items.length;i++)
  //  {
  //    if(items[i].key === this.props.currentKey)
  //    { 
  //      match = "true";
  //     this.setState({tasks:items[i].DeadLines[name]});
  //    }
  //  }
  //  if(match == "false"){
  //    var task = [];
  //    for(let j=0;j<items.length;j++){
  //      task.push(items[j].DeadLines[name]);
  //    }
  //    this.setState({tasks:task});
  //  }
  //  console.log(this.state.tasks);
  // }

  displayToday(name){
    var match = "false";
    let items=this.props.items;
    this.setState({showTodayTasks:!this.state.showTodayTasks});
    for(let i=0;i<items.length;i++)
   {
     if(items[i].key === this.props.currentKey)
     { 
       match = "true";
      this.setState({Todaytasks:items[i].DeadLines[name]});
     }
   }
   if(match == "false"){
    var task = [];
    for(let j=0;j<items.length;j++){
      task.push(items[j].DeadLines[name]);
    }
    this.setState({Todaytasks:task});
  }
  }

  displayTomorrow(name){
    var match = "false";
    let items=this.props.items;
    this.setState({showTomorrowTasks:!this.state.showTomorrowTasks});
    for(let i=0;i<items.length;i++)
   {
     if(items[i].key === this.props.currentKey)
     { 
       match = "true";
      this.setState({Tomorrowtasks:items[i].DeadLines[name]});
     }
   }
   if(match == "false"){
    var task = [];
    for(let j=0;j<items.length;j++){
      task.push(items[j].DeadLines[name]);
    }
    this.setState({Tomorrowtasks:task});
  }
  }

  displaySomeday(name){
    var match = "false";
    let items=this.props.items;
    this.setState({showSomedayTasks:!this.state.showSomedayTasks});
    for(let i=0;i<items.length;i++)
   {
     if(items[i].key === this.props.currentKey)
     { 
       match = "true";
      this.setState({Somedaytasks:items[i].DeadLines[name]});
     }
   }
   if(match == "false"){
    var task = [];
    for(let j=0;j<items.length;j++){
      task.push(items[j].DeadLines[name]);
    }
    this.setState({Somedaytasks:task});
  }
  }


  render() {

    return (
      <div className="Tasks">
      <div className="Space">           
      <strong>{this.props.taskName}</strong>
      </div>

          <div className="Card">
            <div className="Task" onClick={this.displayToday.bind(this,"Today")}>
            Today
            </div>

            <TodayTasks tasks={this.state.Todaytasks} show={this.state.showTodayTasks}/>

            <div className="Task" onClick={this.displayTomorrow.bind(this,"Tomorrow")}>
            Tomorrow
            </div>

            <TomorrowTasks tasks={this.state.Tomorrowtasks} show={this.state.showTomorrowTasks}/>

            <div className="Task" onClick={this.displaySomeday.bind(this,"Someday")}>
            Someday
            </div>

            <SomedayTasks tasks={this.state.Somedaytasks} show={this.state.showSomedayTasks}/>

          </div>

      </div>
    );
  }
}

export default Tasks;
