import React, { Component } from "react";
import DisplayTasks from './DisplayTasks';
import "./Tasks.css";
var tasks=[];
class Tasks extends Component {
  constructor()
  {
    super()
    {
     this.state={
       show:false,
       Today:"",
       Tomorrow:"",
       Someday:""
     }
    }
  }
  display(name){
  var show=!this.state.show;
  var task="";
  var Today="";
  var Tomorrow="";
  var Someday="";
   let items=this.props.items;
   if(show)
   {
   for(let i=0;i<items.length;i++)
   {
     if(items[i].key===this.props.currentKey)
     {
      tasks=items[i].DeadLines[name];
              for(var i=0;i<tasks.length;i++)
        {
          task=task+tasks[i];
          task=task+'\n';
        }
                if(name=="Today")
            {
              Today=task;
            }
            else if(name=="Tomorrow"){
              Tomorrow=task;
            }
            else if(name=="Someday"){
              Someday=task;
            }
     }
   }
   if(tasks == ""){
     for(let i=0;i<items.length;i++){
       tasks = tasks+" "+items[i].DeadLines[name];
       console.log(tasks);
     }
    // let task1=["a","b","c"];
    // let task2=["d","e","f"];
    // task=task1+" "+task2;
    // console.log(task);
   if(name=="Today")
   {
     Today=tasks;
   }
   else if(name=="Tomorrow"){
     Tomorrow=tasks;
   }
   else if(name=="Someday"){
     Someday=tasks;
   }
  }
  }
   this.setState(
   {
     show:show,
     Today:Today,
     Tomorrow:Tomorrow,
     Someday:Someday
   });
  tasks = "";
  }

  render() {

    return (
      <div className="Tasks">
      <div className="Space">           
      <strong>{this.props.taskName}</strong>
      </div>

          <div className="Card">
            <div className="Task" onClick={this.display.bind(this,"Today")}>
            Today 
            <div className="DisplayTasks">{this.state.Today}</div> 
            </div>
            <div className="Task" onClick={this.display.bind(this,"Tomorrow")}>
            Tomorrow
            <div className="DisplayTasks">{this.state.Tomorrow}</div> 
            </div>
            <div className="Task" onClick={this.display.bind(this,"Someday")}>
            Someday
            <div className="DisplayTasks">{this.state.Someday}</div> 
            </div>


          </div>

      </div>
    );
  }
}

export default Tasks;
