import React, { Component } from "react";
import TodayTasks from './TodayTasks';
import "./Tasks.css";

class Tasks extends Component {
  constructor(props){
    super(props);
    this.state = {
     tasks:[],
     showTodayTasks:false
    };
  }

  display(name){
   var match = "false";
   let items=this.props.items;
   if(name === "Today"){
     this.setState({showTodayTasks:!this.state.showTodayTasks});
   }
  this.setState({show:name});
   for(let i=0;i<items.length;i++)
   {
     if(items[i].key === this.props.currentKey)
     { 
       match = "true";
      this.setState({tasks:items[i].DeadLines[name]});
     }
   }
   if(match == "false"){
     var task = [];
     for(j=0;j<items.length;j++){
       task.push(items[i].DeadLines[name]);
     }
     this.setState({tasks:task});
   }
   console.log(this.state.tasks);
  }

  render() {

    return (
      <div className="Tasks">
      <div className="Space">           
      <strong>{this.props.taskName}</strong>
      </div>

          <div className="Card">
            <div className="Task" onClick={this.display.bind(this,"Today")}>
            Today
            </div>

            <TodayTasks tasks={this.state.tasks} show={this.state.showTodayTasks}/>

            <div className="Task" onClick={this.display.bind(this,"Tomorrow")}>
            Tomorrow
            </div>

            {/* <TomorrowTasks tasks={this.state.tasks} show={this.state.show}/> */}

            <div className="Task" onClick={this.display.bind(this,"Someday")}>
            Someday
            </div>

            {/* <SomedayTasks tasks={this.state.tasks} show={this.state.show}/> */}

          </div>

      </div>
    );
  }
}

export default Tasks;
