import React,{Component} from 'react';
import "./TodayTasks.css";

class TomorrowTasks extends Component {
    constructor(props){
        super(props);

        this.displayTasks = this.displayTasks.bind(this);
      }

    displayTasks(item) {
        console.log(item);
        return <div onClick={() => this.setSubTaskValues(item)} className="TodayTaskName" key={item}>{item}</div>
      }

      setSubTaskValues(item){
        this.props.setSubTaskValues(item);
     }

  render() {
     let showHideClassName;
    if (this.props.show) {
      showHideClassName = "TodayTaskList";
    } else {
      showHideClassName = "";

      var taskEntries = this.props.tasks;
      var taskItems = taskEntries.map(this.displayTasks);
    }
    return (
      <div className={showHideClassName} >
       {taskItems}
      </div>
    );
  }
}


export default TomorrowTasks;
