import React,{Component} from 'react';
import "./TodayTasks.css";

class SomedayTasks extends Component {
    constructor(props){
        super(props);

        this.displayTasks = this.displayTasks.bind(this);
      }

    displayTasks(item) {
        console.log(item);
        return <li className="li" key={item}>{item}</li>
      }

  render() {
     let showHideClassName;
    if (this.props.show) {
      showHideClassName = "thelist";
    } else {
      showHideClassName = "";

      var taskEntries = this.props.tasks;
      var taskItems = taskEntries.map(this.displayTasks);
    }
    return (
      <ui className={showHideClassName} >
       {taskItems}
      </ui>
    );
  }
}


export default SomedayTasks;
