import React,{Component} from 'react';
import "./DeleteTodayTasks.css";

class DeleteTodayTasks extends Component {
    constructor(props){
        super(props);

        this.deleteTodayTasks = this.deleteTodayTasks.bind(this);
      }

    deleteTodayTasks(item) {
        console.log(item);
        console.log("entered");
        return <div className="TodayTaskName" key={item}><div className="deleteToday"><img  src="https://img.icons8.com/small/16/000000/delete-forever.png"></img></div><p className="deleteTodayp">{item}</p>  </div>
    }

  render() {
     let showHideClassName;
    if (this.props.show) {
      showHideClassName = "TodayTaskList";
    } else {
      showHideClassName = "";

      var todayTaskEntries = this.props.tasks;
      var todayTaskItems = todayTaskEntries.map(this.deleteTodayTasks);
    }
    return (
      <div className={showHideClassName} >
       {todayTaskItems}
      </div>
    );
  }
}


export default DeleteTodayTasks;
