import React,{Component} from 'react';
import "./TodayTasks.css";

class TodayTasks extends Component {
    constructor(props){
        super(props);

        this.displayTasks = this.displayTasks.bind(this);
      }

    displayTasks(item) {
        return <li className="li">{item}</li>
      }

  render() {
     let showHideClassName;
    if (this.props.show) {
      showHideClassName = "thelist display-block";
    } else {
      showHideClassName = "thelist display-none";

      var taskEntries = this.props.tasks;
      var taskItems = taskEntries.map(this.displayTasks);
    }
    return (
      <ui className={showHideClassName} >
       {taskItems}
      </ui>
    );
  }
}


export default TodayTasks;
